import{_ as a,o as e,c as p,d as r}from"./app.9f3e6cab.js";const h={},d=r('<h1 id="\u6548\u7387" tabindex="-1"><a class="header-anchor" href="#\u6548\u7387" aria-hidden="true">#</a> \u6548\u7387\uFF01</h1><h2 id="\u524D\u8A00" tabindex="-1"><a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a> \u524D\u8A00</h2><p>\u5728\u6587\u5B66\u7684\u4E16\u754C\u91CC\uFF0C\u4E00\u5343\u4E2A\u8BFB\u8005\u773C\u4E2D\u6709\u4E00\u5343\u4E2A\u54C8\u59C6\u96F7\u7279\uFF1B\u5728\u73B0\u5B9E\u7684\u4E16\u754C\u91CC\uFF0C\u65E0\u6570\u7684\u89C2\u70B9\u6C47\u96C6\u6210\u4E86\u5BF9\u4F1F\u4EBA\u7684\u591A\u9762\u8BC4\u4EF7\u3002\u6211\u503E\u6CE8\u4E86\u534A\u5929\u7684\u65F6\u95F4\uFF0C\u7FFB\u5B8C\u4E86\u5173\u4E8E\u9A6C\u65AF\u514B\u7684\u81EA\u4F20\uFF0C\u4E00\u4E2A\u8BA9\u4E16\u4EBA\u77DB\u76FE\u91CD\u91CD\u7684\u4F20\u5947\u4EBA\u7269\u8D70\u8FDB\u4E86\u6211\u7684\u89C6\u91CE\u3002\u4ED6\uFF0C\u662F\u5929\u624D\uFF0C\u8FD8\u662F\u5FFD\u60A0\uFF1F\u8206\u8BBA\u7684\u98CE\u5411\u4F3C\u4E4E\u6C38\u8FDC\u90FD\u65E0\u6CD5\u5F97\u5230\u5B9A\u8BBA\u3002\u7136\u800C\uFF0C\u4F1F\u5927\u7684\u4EBA\u7269\u5F80\u5F80\u90FD\u5E26\u6709\u4E00\u4E1D\u795E\u79D8\u548C\u4E0D\u88AB\u7406\u89E3\u7684\u8272\u5F69\u3002</p><p>\u9A6C\u65AF\u514B\uFF0C\u8FD9\u4F4D\u4EE5\u504F\u6267\u548C\u5F02\u4E4E\u5BFB\u5E38\u800C\u8457\u79F0\u7684\u4F01\u4E1A\u5BB6\uFF0C\u5176\u5B9E\u5E76\u4E0D\u5B64\u5355\u3002\u5F88\u591A\u56FD\u5916\u7684\u4F18\u79C0\u4F01\u4E1A\u5BB6\uFF0C\u90FD\u5E26\u6709\u4E00\u4E9B\u7C7B\u4F3C\u7684\u7279\u8D28\u3002\u4ED6\u4EEC\u7684\u601D\u60F3\u548C\u884C\u4E3A\uFF0C\u5E38\u5E38\u8BA9\u4EBA\u611F\u5230\u4E0D\u53EF\u601D\u8BAE\uFF0C\u4F46\u6B63\u662F\u8FD9\u79CD\u7279\u7ACB\u72EC\u884C\uFF0C\u5851\u9020\u4E86\u4ED6\u4EEC\u7684\u4E0E\u4F17\u4E0D\u540C\u3002\u5373\u4FBF\u7ECF\u5386\u4E86\u521B\u4E1A\u7BA1\u7406\u7684\u4E25\u683C\u78E8\u70BC\uFF0C\u4ED6\u4EEC\u8EAB\u4E0A\u7684\u90A3\u79CD\u504F\u6267\u548C\u8FFD\u6C42\uFF0C\u4F9D\u7136\u6839\u6DF1\u8482\u56FA\u3002</p><p>\u4ED6\u4EEC\u75F4\u8FF7\u4E8E\u6548\u7387\u7684\u63D0\u5347\uFF0C\u538C\u6076\u4EFB\u4F55\u5F62\u5F0F\u7684\u62D6\u6C93\u548C\u77EB\u60C5\u3002\u4E8E\u4ED6\u4EEC\u800C\u8A00\uFF0C\u76F4\u63A5\u3001\u660E\u786E\u7684\u4EA4\u6D41\u662F\u6C9F\u901A\u7684\u57FA\u77F3\u3002\u5F02\u6B65\u6C9F\u901A\uFF0C\u5982\u901A\u8FC7\u90AE\u7BB1\u4EA4\u6D41\uFF0C\u4E0D\u4EC5\u4EC5\u662F\u4E3A\u4E86\u8282\u7701\u65F6\u95F4\uFF0C\u66F4\u662F\u4E3A\u4E86\u4FDD\u6301\u601D\u8DEF\u7684\u6E05\u6670\u548C\u8FDE\u8D2F\u3002\u5728\u4ED6\u4EEC\u7684\u4E16\u754C\u91CC\uFF0C\u6548\u7387\u4E0D\u4EC5\u4EC5\u662F\u4E00\u79CD\u8FFD\u6C42\uFF0C\u66F4\u662F\u4E00\u79CD\u4FE1\u4EF0\u3002</p><p>\u63A5\u4E0B\u6765\u6211\u4EEC\u6765\u770B\u770B\u4E00\u4E2A\u5C0F\u56E2\u961F\uFF0C\u6216\u8005\u662F\u4E2A\u4EBA\u7684\u6548\u7387\u5982\u4F55\u505A\u5230\u6781\u81F4\u3002</p><p><strong>Basecamp \u56E2\u961F\uFF1A</strong></p><p>\u4ECE\u4E09\u4E2A\u4EBA\u4E0D\u8D77\u773C\u7684\u56E2\u961F\uFF0C\u505A\u9879\u76EE\u7BA1\u7406\u8F6F\u4EF6\uFF0C\u521B\u7ACB\u4E86 basecamp\uFF0C\u6BCF\u5E74\u7684\u5229\u6DA6\u4E0A\u767E\u7F8E\u5143\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u4ED6\u4EEC\u8FD8\u51FA\u552E\u4E86\u5176\u4ED6\u7684\u5728\u7EBF\u5DE5\u5177\uFF0C\u6BD4\u5982\u8BF4 Highrise \uFF0C\u662F\u4E00\u5957 CRM \u7CFB\u7EDF\u3002</p><p>\u4ED6\u4EEC\u7684\u56E2\u961F\u5982\u4F55\u4FDD\u8BC1\u7684\u662F\u5982\u6B64\u5C0F\u800C\u5F3A\u5927\u7684\u5462\uFF1F</p><p>**\u6709\u4EBA\u8BF4\u4ED6\u4EEC\u4E00\u5B9A\u6709\u5438\u7EB3\u4F18\u79C0\u7684\u7BA1\u7406\u4F53\u7CFB\uFF0C\u6BD4\u5982\u8BF4\u4F18\u8D28\u7684\u5F00\u53D1\u6D41\u7A0B\uFF0C\u7BA1\u7406\u6D41\u7A0B\uFF0C\u4ECE\u4E0A\u5230\u4E0B\u6709\u4E00\u4E2A\u6807\u51C6\u4F53\u7CFB\uFF0C\u6709\u4E00\u5957\u4F1A\u8BAE\u548C\u7BA1\u7406\u6A21\u5F0F\u3002**\u5176\u5B9E\u5E76\u4E0D\u662F\u7684\uFF0C\u4ED6\u4EEC\u6CA1\u6709\u4F1A\u8BAE\uFF0C\u6CA1\u6709\u5E7F\u544A\uFF0C\u539F\u7406\u4E86\u9500\u552E\u4EBA\u5458\u548C \u201C\u73B0\u5B9E\u4E16\u754C\u201D \u7684\u60C5\u51B5\u4E0B\uFF0C\u4F9D\u65E7\u5F88\u706B\u3002</p><p>\u751A\u81F3\uFF0C\u4ED6\u4EEC\u7684\u5458\u5DE5\u90FD\u662F remote jobs \u6A21\u5F0F\uFF0C\u8FD9\u662F\u4E00\u4E2A\u5F88\u5148\u8FDB\u7684\u7406\u5FF5\u3002\u5982\u4F55\u8BA9\u5206\u6563\u5728\u4E24\u4E2A\u5927\u6D32\u7684\u516B\u5EA7\u57CE\u5E02\u8001\u6B7B\u4E0D\u76F8\u5F80\u6765\u7684\u5730\u65B9\u6765\u534F\u540C\u5DE5\u4F5C\uFF0C\u5982\u4F55\u5728\u6CA1\u6709\u8D22\u52A1\u89C4\u5212\u548C\u4E94\u5E74\u8BA1\u5212\u7684\u6A21\u5F0F\u4E0B\u751F\u5B58\uFF0C\u662F\u4E00\u4E2A\u503C\u5F97\u601D\u8003\u7684\u8FC7\u7A0B\u3002</p><p>\u9664\u4E86\u8FD9\u4E2A\uFF0C\u6211\u4EEC\u518D\u8C08\u4E00\u8C08\u7C73\u54C8\u6E38\u7684\u521B\u4E1A\u53F2\uFF0C\u7C73\u54C8\u6E38\u7684\u6210\u957F\u5728\u56FD\u5185\u4E5F\u662F\u4E00\u4E2A\u4F20\u5947\u3002</p><p>\u4ECE\u5F00\u59CB 2009\u5E74\u51AC\u5929\uFF0C\u8521\u6D69\u5B87\u3001\u5218\u4F1F\u3001\u7F57\u5B87\u7693\u53C2\u52A0\u4E2D\u79D1\u9662\u8BA1\u7B97\u6240\u7B2C\u4E8C\u5C4A\u6280\u672F\u521B\u65B0\u5927\u8D5B\uFF0C\u9760\u3010\u57FA\u4E8EFlash\u7684\u63D2\u4EF6\u5F0F\u7F51\u7EDC\u6E38\u620F\u5E73\u53F0\u3011\u9879\u76EE\u62FF\u5230\u4E8620\u4E07\u5143\u7684\u5956\u5B66\u91D1\u3002</p><p>2021 \u5E74\uFF0C\u7ECF\u8FC7\u51E0\u6B21\u5927\u8D5B\uFF0C\u60F3\u8981\u505AACG\uFF0C\u5148\u4EE5\u6E38\u620F\u5207\u5165\u8FD9\u4E2A\u601D\u8DEF\u9010\u6E10\u6E05\u6670\uFF0C2011\u5E741\u6708\uFF0C\u51E0\u4E2A\u4EBA\u5728\u5BBF\u820D\u91CC\uFF0C\u6B63\u5F0F\u6210\u7ACB\u4E86\u201C\u7C73\u54C8\u6E38\u5DE5\u4F5C\u5BA4\u201D\u8FD9\u6BB5\u65F6\u671F\u4ED6\u4EEC\u627E\u6765\u4E86\u7F8E\u672F\u8D1F\u8D23\u4EBAcici\u5F20\u5E86\u534E\uFF0C\u56E2\u961F\u75314\u4EBA\u53D8\u6210\u4E865\u4EBA</p><h2 id="\u653E\u4E0B\u8D1F\u62C5" tabindex="-1"><a class="header-anchor" href="#\u653E\u4E0B\u8D1F\u62C5" aria-hidden="true">#</a> \u653E\u4E0B\u8D1F\u62C5</h2><h3 id="\u521B\u4E1A\u4EE5\u53CA\u521B\u4E1A\u56E2\u961F\u7684\u8D8B\u52BF" tabindex="-1"><a class="header-anchor" href="#\u521B\u4E1A\u4EE5\u53CA\u521B\u4E1A\u56E2\u961F\u7684\u8D8B\u52BF" aria-hidden="true">#</a> \u521B\u4E1A\u4EE5\u53CA\u521B\u4E1A\u56E2\u961F\u7684\u8D8B\u52BF</h3><p>\u968F\u7740AI\u4EBA\u5DE5\u667A\u80FD\u65F6\u4EE3\u7684\u6765\u4E34\uFF0C\u5174\u8DA3\u63A8\u8350\u7B97\u6CD5\u3001\u77ED\u89C6\u9891\u3001\u76F4\u64AD\u5E26\u8D27\u7B49\u65B0\u6280\u672F\u65B0\u5E94\u7528\u7684\u5D1B\u8D77\uFF0C\u5927\u4F17\u4F53\u9A8C\u6570\u5B57\u751F\u6D3B\u7684\u65B9\u5F0F\u6B63\u5728\u53D1\u751F\u5FEB\u901F\u53D8\u5316\uFF0C\u8FD9\u4E5F\u6B63\u662F\u4F20\u7EDF\u4F01\u4E1A\u5347\u7EA7\u65B0\u8D5B\u9053\u7684\u6700\u597D\u5951\u673A\u3002</p><p>\u6BD4\u5982\uFF1A\u524D\u51E0\u5E74\uFF0C\u5F53\u5F88\u591A\u4F01\u4E1A\u8FD8\u5728\u4EBA\u5DE5\u6253\u8425\u9500\u7535\u8BDD\u505A\u964C\u62DC\u62D3\u5BA2\u7684\u65F6\u5019\uFF0CAI\u8BED\u97F3\u667A\u80FD\u7535\u8BDD\u5DF2\u7ECF\u53EF\u4EE5\u5F00\u59CB\u4E3A\u4F01\u4E1A\u505A\u7FA4\u547C\u673A\u5668\u4EBA\uFF0C\u4E00\u59291500\u4E2A\u7535\u8BDD\u547C\u51FA\u9876\u7684\u4E0A\u4E00\u4E2A10\u4EBA\u56E2\u961F\u3002</p><p>SocialCapital CEO Chamath Palihapitiya \u4E4B\u524D\u5728\u4E00\u4E2A\u64AD\u5BA2\u91CC\uFF0C\u8BA8\u8BBA\u4E86 AI \u5BF9\u4E8E\u521B\u4E1A\u3001\u6295\u8D44\u548C\u516C\u53F8\u7EC4\u5EFA\u7684\u5F71\u54CD\uFF0C\u4ED6\u8BF4 AI \u6B63\u5728\u4EE5\u524D\u6240\u672A\u6709\u7684\u901F\u5EA6\u8FED\u4EE3\uFF0C\u521B\u65B0\u5728\u4EE5\u5929\u548C\u5468\u4E3A\u5355\u4F4D\u8FDB\u884C\u3002\u8FD9\u5C06\u6539\u53D8\u516C\u53F8\u7684\u521B\u7ACB\u65B9\u5F0F\uFF0C<strong>\u8FBE\u5230 MVP \u7684\u56E2\u961F\u89C4\u6A21\u4F1A\u8D8A\u6765\u8D8A\u5C0F\uFF0C 3~4 \u4E2A\u4EBA\uFF0C\u800C\u4E0D\u662F\u4E4B\u524D\u901A\u5E38\u7684 40 \u6216 50 \u4EBA\u7684\u516C\u53F8</strong>\u2014\u2014\u8FD9\u610F\u5473\u7740 VC \u548C\u4ED6\u4EEC\u7684\u6295\u8D44\u6A21\u578B\u9700\u8981\u8DDF\u4E0A\u8FD9\u4E9B\u53D8\u5316\u3002</p><p>Every \u7684\u521B\u59CB\u4EBA Nathan Baschez \u8BA4\u4E3A\uFF0C<strong>\u5728 AI \u65F6\u4EE3\uFF0C\u4E00\u79CD\u65B0\u578B\u7684\u521B\u4E1A\u516C\u53F8\u5F62\u6001\u6B63\u5728\u8BDE\u751F\uFF0C\u5176\u7279\u70B9\u662F\uFF1Asmaller\u3001faster\u3001cheaper and weirder</strong>\uFF08\u66F4\u5C0F\u3001\u66F4\u5FEB\u3001\u66F4\u4FBF\u5B9C\u3001\u66F4\u5947\u602A\uFF09\u3002</p><p>\u6240\u4EE5\u603B\u7ED3\u4E0A\u9762\u7684\u4FE1\u606F\uFF0C\u6211\u4EEC\u80FD\u770B\u5230\u7684\u662F\uFF0C\u8FD9\u4E2A\u65F6\u4EE3\uFF0C\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u521B\u4E1A\uFF0C\u8FC7\u53BB\u65E0\u6CD5\u4F01\u53CA\u7684\u5DE5\u5177\u5982\u4ECA\u90FD\u5782\u624B\u53EF\u5F97\u3002\u52A0\u4E0A\u5F3A\u5927\u7684 AI \u5DE5\u5177\uFF0C\u73B0\u5728\u4E00\u4E2A\u4EBA\u80FD\u62B5\u5F97\u4E0A\u4E4B\u524D\u4E00\u4E2A\u56E2\u961F\u7684\u5DE5\u4F5C\u91CF\u3002</p><h3 id="\u4ECE\u6210\u529F\u4E2D\u5438\u53D6\u517B\u5206" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u6210\u529F\u4E2D\u5438\u53D6\u517B\u5206" aria-hidden="true">#</a> \u4ECE\u6210\u529F\u4E2D\u5438\u53D6\u517B\u5206</h3><p>\u4E2D\u56FD\u4EBA\u5E38\u5E38\u8BF4\uFF1A\u5931\u8D25\u662F\u6210\u529F\u5B50\u6BCD\uFF0C\u521B\u4E1A\u5012\u95ED\u8005\u5341\u6709\u516B\u4E5D\uFF0C\u8FD8\u6709\u4EBA\u8BF4\u5931\u8D25\u80FD\u94F8\u9020\u54C1\u884C\uFF0C\u751A\u81F3\u8FD8\u6709\u4EBA\u8BF4\uFF0C\u65E9\u6B7B\u65E9\u8D85\u751F\uFF0C\u6240\u4EE5\u65E9\u70B9\u5931\u8D25\u3002</p><p>\u8FD9\u4E9B\u89C2\u70B9\u5F88\u611A\u6627\u3002\u4EBA\u8981\u4ECE\u5931\u8D25\u4E2D\u5438\u53D6\u6559\u8BAD\uFF0C\u90A3\u4E48\u5230\u5E95\u4ECE\u5931\u8D25\u4E2D\u5438\u53D6\u5230\u7684\u4EC0\u4E48\uFF1F\u6211\u4EEC\u5F92\u6B65\u7684\u65F6\u5019\u7B2C\u4E00\u6B21\u559C\u6B22\u5E26\u5F88\u591A\u4E1C\u897F\uFF0C\u6700\u540E\u53D1\u73B0\u8D70\u4E0D\u52A8\u4E86\u3002\u4E8E\u662F\u6211\u4EEC\u5438\u53D6\u5230\u6559\u8BAD\uFF0C\u7B2C\u4E8C\u6B21\u5C11\u5E26\u4E00\u4E9B\u3002\u4F46\u662F\u8FD9\u6837\u7684\u610F\u4E49\u5F88\u5927\u5417\u3002\u6211\u8FD8\u662F\u4E0D\u77E5\u9053\u7B2C\u4E8C\u6B21\u8BE5\u5E26\u4EC0\u4E48\uFF0C\u8BE5\u600E\u4E48\u6837\u624D\u80FD\u987A\u5229\u8D70\u5B8C\u3002</p><p>\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u6211\u4EEC\u66F4\u5E94\u8BE5\u4ECE\u6210\u529F\u4E2D\u5438\u53D6\u517B\u5206\uFF0C\u6210\u529F\u624D\u662F\u771F\u6B63\u9760\u5F97\u4F4F\u7684\u6559\u6750\u3002\u505A\u6210\u4E00\u4EF6\u4E8B\u60C5\u540E\uFF0C\u4F60\u5C31\u4F1A\u77E5\u9053\u8FD9\u6761\u8DEF\u884C\u5F97\u901A\uFF0C\u7136\u540E\u4F60\u4E0B\u6B21\u5C31\u4F1A\u518D\u6B21\u8FD9\u4E48\u505A\uFF0C\u800C\u4E0B\u4E00\u6B21\uFF0C\u4F60\u4E5F\u8BB8\u4F1A\u505A\u7684\u66F4\u68D2\u3002</p><p>\u54C8\u4F5B\u5B66\u6821\u7814\u7A76\u8868\u660E\uFF1A<strong>\u90A3\u4E9B\u5DF2\u7ECF\u6210\u529F\u7684\u4F01\u4E1A\u5BB6\u4EEC\u518D\u521B\u8F89\u714C\u7684\u53EF\u80FD\u6027\u8FDC\u6BD4\u5E38\u4EBA\u5927\u5F97\u591A\u3002\u6240\u4EE5\u6210\u529F\u662F\u4E00\u4E2A\u6781\u5177\u73B0\u5B9E\u4EF7\u503C\u7684\u5B9D\u8D35\u7ECF\u9A8C\u3002</strong></p><h3 id="\u8BA1\u5212\u5373\u731C\u60F3" tabindex="-1"><a class="header-anchor" href="#\u8BA1\u5212\u5373\u731C\u60F3" aria-hidden="true">#</a> \u8BA1\u5212\u5373\u731C\u60F3</h3><p>\u6211\u4EEC\u77E5\u9053\uFF0C\u5C24\u5176\u662F\u5927\u5382\u51FA\u8EAB\u7684\u4EBA\u6765\u8BF4\uFF0C\u5BF9\u4EFB\u4F55\u4E00\u4E2A\u4EFB\u52A1\u90FD\u559C\u6B22\u505A\u8BE6\u7EC6\u7684\u8BA1\u5212\uFF0C\u7F16\u5199\u8BA1\u5212\u4E66\uFF0C\u4F46\u662F\u505A\u8BA1\u5212\uFF0C\u5176\u5B9E\u4E5F\u662F\u7C7B\u4F3C\u4E8E\u731C\u60F3\uFF0C\u6211\u4EEC\u628A\u731C\u60F3\u53D8\u6210\u8BA1\u5212\uFF0C\u662F\u4E00\u79CD\u6682\u65F6\u4E0D\u786E\u5B9A\u56E0\u7D20\uFF0C\u5C31\u7B49\u540C\u4E8E\u505A\u8BA1\u5212\u5C31\u662F\u7528\u8FC7\u53BB\u6765\u63A8\u52A8\u672A\u6765\uFF0C\u7ED9\u6211\u4EEC\u5E26\u4E0A\u4E86\u773C\u7F69\u3002\u8BA1\u5212\u548C\u5373\u5174\u53D1\u6325\u662F\u4E0D\u5408\u62CD\u7684\u3002</p><p>\u6211\u4EEC\u5FC5\u987B\u8981\u5373\u5174\u53D1\u6325\uFF0C\u5FC5\u987B\u8981\u6293\u4F4F\u6BCF\u4E00\u4E2A\u8FCE\u9762\u800C\u6765\u7684\u673A\u9047\u3002\u957F\u671F\u8BA1\u5212\u4E00\u5B9A\u662F\u4E0D\u53EF\u9760\u7684\uFF0C\u6240\u4EE5\u6211\u5728 \u5BF9 openim \u7684\u672A\u6765\u8BA1\u5212\u4E2D\uFF0C\u4E00\u5B9A\u53EA\u662F\u8BA1\u5212\u5F53\u524D\u7248\u672C\uFF0C\u4EE5\u53CA\u4E0B\u4E00\u671F\u7248\u672C\u7684\uFF0C \u4E0D\u5FC5\u8FC7\u591A\u89C4\u5212\uFF0C\u6BEB\u65E0\u610F\u4E49\u3002</p><h3 id="\u4F55\u5FC5\u58EE\u5927" tabindex="-1"><a class="header-anchor" href="#\u4F55\u5FC5\u58EE\u5927" aria-hidden="true">#</a> \u4F55\u5FC5\u58EE\u5927</h3><p>\u4E00\u4E2A\u5C0F\u7684\u516C\u53F8\u80FD\u6709\u591A\u5C0F\uFF1F\u5C0F\u516C\u53F8\u5F80\u5F80\u5728\u4EBA\u4EEC\u7684\u773C\u4E2D\u90FD\u662F\u4E0D\u5C51\u4E00\u987E\u7684\uFF0C\u5F53\u4F60\u7684\u516C\u53F8\u5F88\u5927\uFF0C\u4F60\u8EAB\u8FB9\u7684\u4EB2\u621A\u5C31\u4F1A\u5F88\u81EA\u7136\u7684\u8BA4\u4E3A\u4F60\u7684\u516C\u53F8\u4E00\u5B9A\u662F\u5F88\u5F3A\u5927\u7684\uFF0C\u5F88\u4F18\u79C0\u7684\uFF0C\u95E8\u69DB\u5F88\u9AD8\u7684\u3002\u800C\u5982\u679C\u4F60\u7684\u516C\u53F8\u4EC5\u4EC5\u662F\u51E0\u4EBA\u6216\u8005\u662F\u5341\u51E0\u4EBA\u7684\u5C0F\u516C\u53F8\uFF0C\u90A3\u4E48\u4ED6\u4EEC\u80AF\u5B9A\u4F1A\u8BA4\u4E3A\u662F\u5C0F\u5B69\u628A\u620F\u3002</p><p>\u4E3A\u4EC0\u4E48\u4F1A\u8FD9\u6837\uFF1F\u4E2D\u56FD\u4EBA\u6F5C\u610F\u8BC6\u8BA4\u4E3A\u6269\u5F20\u624D\u662F\u4E00\u4E2A\u516C\u53F8\u5F3A\u5927\u7684\u56E0\u7D20\uFF0C\u5305\u62EC\u4EBA\u5458\u89C4\u6A21\u7684\u6269\u5F20\u3002\u4F46\u662F\u6269\u5C55\u610F\u5473\u7740\u6210\u672C\u7684\u6269\u5F20\uFF0C\u4F46\u662F\u6536\u5165\u5374\u4E0D\u4E00\u5B9A\u4F1A\u66F4\u591A\u3002</p><p>\u5C0F\u516C\u53F8\u7684\u68A6\u60F3\u90FD\u662F\u60F3\u53D8\u5927\u3002\u5927\u516C\u53F8\u7684\u68A6\u60F3\u662F\u8FFD\u6C42 devops \uFF0C\u66F4\u52A0\u654F\u6377\u548C\u7075\u6D3B\u3002</p><p>\u53EA\u8981\u5C0F\u516C\u53F8\u80FD\u591F\u4FDD\u6301\u76C8\u5229\uFF0C\u65E0\u8BBA\u89C4\u6A21\u7684\u5927\u5C0F\uFF0C\u90FD\u662F\u503C\u5F97\u9A84\u50B2\u7684\u3002</p><h3 id="\u5DE5\u4F5C\u72C2" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C\u72C2" aria-hidden="true">#</a> \u5DE5\u4F5C\u72C2</h3><p>\u5DE5\u4F5C\u72C2\u7684\u884C\u4E3A\u5728\u5F88\u591A\u65F6\u5019\u662F\u6CA1\u6709\u5FC5\u8981\u7684\uFF0C\u8FC7\u591A\u7684\u5DE5\u4F5C\u4E0D\u4EE3\u8868\u4F60\u5BF9\u9879\u76EE\u66F4\u4E13\u6CE8\uFF0C\u4E5F\u4E0D\u4EE3\u8868\u4F60\u505A\u4E86\u66F4\u591A\u7684\u8D21\u732E\uFF0C\u800C\u662F\u4EC5\u4EC5\u610F\u5473\u7740\u4F60\u505A\u4E86\u66F4\u591A\u7684\u6D3B\u3002</p><p>\u6B63\u786E\u7684\u6A21\u5F0F\uFF0C\u5E76\u4E0D\u662F\u65E0\u8111\u7684\u52A0\u73ED\uFF0C\u5982\u679C\u53EA\u662F\u4E3A\u4E86\u5DE5\u4F5C\u800C\u5DE5\u4F5C\uFF0C\u90A3\u4E48\u4F60\u5C31\u4F1A\u4E27\u5931\u5224\u65AD\u529B\uFF0C\u4F60\u7684\u4EF7\u503C\u89C2\u548C\u51B3\u7B56\u65B9\u5F0F\u90FD\u662F\u626D\u66F2\u7684\u3002\u4F60\u6CA1\u6709\u529E\u6CD5\u53BB\u5224\u65AD\u54EA\u4E9B\u5DE5\u4F5C\u662F\u503C\u5F97\u505A\uFF0C\u54EA\u4E9B\u5DE5\u4F5C\u662F\u8BE5\u653E\u5F03\u7684\uFF0C\u6700\u540E\u81EA\u5DF1\u7B4B\u75B2\u529B\u5C3D\uFF0C\u800C\u4E00\u4E2A\u7B4B\u75B2\u529B\u5C3D\u7684\u4EBA\u662F\u65E0\u6CD5\u505A\u51FA\u660E\u667A\u7684\u51B3\u5B9A\u3002</p><p>\u6B63\u786E\u7684\u65B9\u5F0F\uFF0C\u662F\u65E9\u65E9\u7684\u627E\u5230\uFF0C\u5E76\u4E14\u89E3\u51B3\u95EE\u9898\uFF0C\u4E0B\u73ED\u4F11\u606F\u54C8\u54C8\u54C8\uFF0C\u5DE5\u4F5C\u662F\u505A\u4E0D\u5B8C\u7684\uFF0C\u6240\u4EE5\u4E0D\u8981\u901A\u8FC7\u52A0\u73ED\u7684\u5F62\u5F0F\u8BA9\u81EA\u5DF1\u89E3\u8131\uFF0C\u7559\u70B9\u65F6\u95F4\u53BB\u601D\u8003\u5BF9\u7B56\u6216\u8005\u4F18\u5316\u65B9\u6848\uFF0C\u66F4\u6709\u5229\u4E8E\u66F4\u5FEB\u66F4\u597D\u7684\u5DE5\u4F5C\u3002</p><h3 id="\u521B\u59CB\u4EBA\u548C\u4F01\u4E1A\u5BB6" tabindex="-1"><a class="header-anchor" href="#\u521B\u59CB\u4EBA\u548C\u4F01\u4E1A\u5BB6" aria-hidden="true">#</a> \u521B\u59CB\u4EBA\u548C\u4F01\u4E1A\u5BB6</h3><p>\u4F01\u4E1A\u5BB6\u8FD9\u4E2A\u8FC7\u6C14\u5F88\u4E45\u7684\u8BCD\u8BED\u5DF2\u7ECF\u9000\u51FA\u5386\u53F2\u7684\u821E\u53F0\u4E86\uFF0C\u5B83\u80CC\u8D1F\u4E86\u592A\u591A\u7684\u6C89\u91CD\u8D1F\u62C5\u3002\u5B83\u7ED9\u4EBA\u7684\u611F\u89C9\u5C31\u662F\u4E00\u4E2A\u5B9E\u884C\u4F1A\u5458\u51C6\u5165\u5236\u5EA6\u7684\u79C1\u4EBA\u4FF1\u4E50\u90E8\uFF0C\u4E0D\u662F\u8C01\u90FD\u80FD\u8FDB\u6765\u7684\uFF0C\u4F46\u662F\u6211\u4EEC\u5E94\u8BE5\u9F13\u52B1\u6BCF\u4E00\u4E2A\u4EBA\u90FD\u53BB\u5F00\u521B\u81EA\u5DF1\u7684\u4E8B\u4E1A\u3002</p><p>\u65B0\u751F\u4EE3\u521B\u59CB\u4EBA\uFF0C\u4ED6\u4EEC\u53EA\u662F\u559C\u6B22\u7528\u81EA\u5DF1\u7684\u65B9\u5F0F\u505A\u81EA\u5DF1\u7684\u4E8B\u60C5\uFF0C\u4ECE\u800C\u83B7\u53D6\u5230\u56DE\u62A5\u3002</p><p>\u6240\u4EE5\u6211\u4EEC\u628A\u8FD9\u4E2A\u79F0\u547C\u66FF\u6362\u4E3A\u66F4\u52A0\u811A\u8E0F\u5B9E\u5730\u7684\u79F0\u547C\uFF0C\u53EB\u521B\u59CB\u4EBA\uFF0C\u6BCF\u4E00\u4E2A\u521B\u7ACB\u81EA\u5DF1\u4E8B\u4E1A\u7684\u4EBA\u90FD\u662F\u521B\u59CB\u4EBA\u3002\u4E0D\u9700\u8981 MBA\uFF0C\u8BC1\u4E66\uFF0C\u897F\u88C5\u7B49\u88C5\u9970\uFF0C\u4F60\u53EA\u9700\u8981\u4E00\u70B9\u70B9\u521B\u610F\uFF0C\u52A0\u4E00\u70B9\u70B9\u81EA\u4FE1\uFF0C\u4EE5\u53CA\u4E00\u70B9\u70B9\u52C7\u6C14\uFF0C\u5C31\u53EF\u4EE5\u542F\u7A0B\u4E86\u3002</p><h2 id="\u5F00\u59CB\u884C\u52A8\u5427" tabindex="-1"><a class="header-anchor" href="#\u5F00\u59CB\u884C\u52A8\u5427" aria-hidden="true">#</a> \u5F00\u59CB\u884C\u52A8\u5427</h2><p>!!!</p>',45),i=[d];function n(t,s){return e(),p("div",null,i)}const o=a(h,[["render",n],["__file","137.html.vue"]]);export{o as default};
